def log_prior(theta):
    omega, alfa, beta, masa, delta = theta
    if 0.0 < omega < 1.0 and 0.0 < alfa < 1.0 and 3 < beta < 4 and -20 < masa < -19 and -1.0 < delta < 0.0:
        prior = 0
    else:
        prior = -np.inf
    return prior

def log_probability(theta):
    lp = log_prior(theta)
    if not np.isfinite(lp):
        return -np.inf
    log_prob = lp + log_likelihood_mod(theta)
    return log_prob
